import React from "react";

import TipModal from "./TipModal.js";
const userbandsURL = "http://localhost:3000/userbands";
const BandShowURL = "http://localhost:3000/bands/";
const userURL = "http://localhost:3000/users/";
class Tip extends React.Component {
  constructor() {
    super();
    this.state = {
      user: [],
      userwallet: 0
    };
  }
  componentWillMount() {
    fetch(`${userURL}${window.localStorage.getItem("id")}`, {
      method: "GET",
      headers: {
        Authorization: `Bearer ${window.localStorage.getItem("token")}`
      }
    })
      .then(res => res.json())
      .then(data => {
        this.setState({
          user: data,
          userwallet: data.wallet
        });
      });
  }

  componentDidMount() {}
  render() {
    return (
      <div>
        <TipModal userWallet={this.state.userwallet} user={this.state.user} />
      </div>
    );
  }
}
export default Tip;
