import React, { Component } from "react";

const profileURL = "http://localhost:3000/profile";
class MainPage extends Component {
  constructor() {
    super();
    this.state = {
      user: [],
      bands: []
    };
  }
  componentDidMount() {
    // fetch(bandsURL, {
    //   method: "GET",
    //   headers: {
    //     Authorization: `Bearer ${window.localStorage.getItem("token")}`
    //   }
    // })
    //   .then(res => res.json())
    //   .then(data => {
    //     debugger;
    //     this.setState({
    //       bands: data
    //     });
    //   });
    fetch(profileURL, {
      method: "GET",
      headers: {
        Authorization: `Bearer ${window.localStorage.getItem("token")}`
      }
    })
      .then(res => res.json())
      .then(data => {
        this.setState({
          user: data.user,
          bands: data.bands
        });
      });
  }
  render() {
    return (
      <div>
        <h1>Welcome back, {window.localStorage.getItem("name")}!</h1>
      </div>
    );
  }
}
export default MainPage;
