import React from "react";
import Card from "react-bootstrap/Card";
import CardDeck from "react-bootstrap/CardGroup";
import moment from "moment";
const BandShowURL = "http://localhost:3000/bands/";

class MainpageCard extends React.Component {
  constructor() {
    super();
    this.state = {
      details: [],
      banddet: [],
      venuedet: [],
      showdet: [],
      genredet: []
    };
  }
  componentDidMount() {
    fetch(`${BandShowURL}${this.props.band}`, {
      method: "GET",
      headers: {
        Authorization: `Bearer ${window.localStorage.getItem("token")}`
      }
    })
      .then(res => res.json())
      .then(data => {
        this.setState({
          details: data,
          banddet: data.band,
          showdet: data.shows,
          venuedet: data.venues,
          genredet: data.genres
        });
      });
  }

  render() {
    return (
      <div>
        <Card>
          <Card.Body>
            <Card.Title>{this.state.banddet.name}</Card.Title>
            <Card.Subtitle>
              {this.state.genredet.map(genre => {
                return genre.name;
              })}{" "}
            </Card.Subtitle>
            <Card.Text>
              <ul>
                {this.state.venuedet.map(venue => {
                  return (
                    <li>
                      {venue.name} | {venue.location} <br></br>
                      {this.state.showdet.map(show => {
                        if (show.venue_id === venue.id)
                          return moment(show.time).format(
                            " hh:mm A, MMMM Do, YYYY "
                          );
                      })}
                    </li>
                  );
                })}
              </ul>
            </Card.Text>
            <Card.Footer>
              {" "}
              <iframe
                src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3446.105755826408!2d-97.72910298491783!3d30.262567815190284!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x8644b5b11e69bfdb%3A0x3ba64fd6fe2cc989!2sThe%20White%20Horse!5e0!3m2!1sen!2sus!4v1572984818092!5m2!1sen!2sus"
                width="400"
                height="300"
                frameborder="0"
                allowfullscreen=""
              ></iframe>{" "}
              | bandprofile page
            </Card.Footer>
          </Card.Body>
        </Card>
      </div>
    );
  }
}
export default MainpageCard;
