import React from "react";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/row";
import Col from "react-bootstrap/Col";
import Button from "react-bootstrap/Button";
import moment from "moment";
import Tip from "../components/Tip.js";
const BandShowURL = "http://localhost:3000/bands/";
const userbandsURL = "http://localhost:3000/userbands";
class BandProfile extends React.Component {
  constructor() {
    super();
    this.state = {
      band: [],
      venues: [],
      shows: [],
      genres: [],
      love: false
    };
  }
  componentDidMount() {
    fetch(`${BandShowURL}${window.localStorage.getItem("bandID")}`, {
      method: "GET",
      headers: {
        Authorization: `"Bearer ${window.localStorage.getItem("token")}`
      }
    })
      .then(res => res.json())
      .then(data => {
        this.props.getstate(data);
        this.setState({
          band: data.band,
          venues: data.venues,
          shows: data.shows,
          genres: data.genres
        });
      });
  }
  loveClick = () => {
    this.setState({
      buttonclicked: !this.state.but
    });
  };
  followFunction = event => {
    event.preventDefault();
    let body = this.state;
    body.band_id = parseInt(this.state.band.id);
    body.user_id = parseInt(window.localStorage.getItem("id"));
    const configOBJ = {
      method: "POST",
      headers: {
        "content-type": "application/json",
        accept: "application/json",
        Authorization: `Bearer ${window.localStorage.getItem("token")}`
      },
      body: JSON.stringify(body)
    };
    fetch(userbandsURL, configOBJ)
      .then(res => res.json())
      .then(json => {
        console.log(json);
      })
      .catch(error => console.log(error));
    window.location.assign("http://localhost:3001/MainPage");
  };

  render() {
    return (
      <div className="band_profile">
        <div>
          <Container>
            <Row>
              <Col>{this.state.band.name}</Col>
              <Col>
                {this.state.genres.map(genre => {
                  return genre.name;
                })}
              </Col>
            </Row>
            <Row>
              <Col>
                <ul>
                  {this.state.venues.map(venue => {
                    return (
                      <li key={venue.id}>
                        {venue.name}{" "}
                        {this.state.shows.map(show => {
                          if (show.venue_id === venue.id) {
                            return moment(show.time).format(
                              " hh:mm A, MMMM Do, YYYY "
                            );
                          }
                        })}
                      </li>
                    );
                  })}
                </ul>
                {`Love given $ ${this.state.band.wallet}`}
              </Col>
              <Col></Col>
            </Row>
          </Container>

          <Button
            variant="outline-dark"
            onClick={event => this.followFunction(event)}
          >
            Follow!
          </Button>
          <Button variant="outline-dark">Send Some Love</Button>
        </div>
        <>
          <Tip />
        </>
      </div>
    );
  }
}
export default BandProfile;
