import React from "react";
import TipModal from "./TipModal.js";
const userbandsURL = "http://localhost:3000/userbands";

class Followed extends React.Component {
  constructor() {
    super();
    this.state = {
      userbands: [],
      modal: false
    };
  }

  componentDidMount() {
    fetch(userbandsURL, {
      method: "GET",
      headers: {
        Authorization: `Bearer ${window.localStorage.getItem("token")}`
      }
    })
      .then(res => res.json())
      .then(data => {
        console.log(data.userbands);

        this.setState({
          userbands: data.userbands
        });
      });
  }
  render() {
    return (
      <div>
        {this.state.userbands.map(userband => {
          return <TipModal userband={this.state.userbands} />;
        })}
      </div>
    );
  }
}

export default Followed;
