import React from "react";
import Card from "react-bootstrap/Card";
const bandURL = "http://localhost:3000/bands/";

const genresarray = {
  country: require("../country.jpg"),
  blues: require("../blues.jpg"),
  classical: require("../classical.jpg"),
  electronic: require("../electronic.jpeg"),
  hiphop: require("../hiphop.jpeg"),
  latin: require("../latin.jpg"),
  reggae: require("../reggae.jpg"),
  rock: require("../rock.jpg"),
  soul: require("../soul.jpg"),
  world: require("../world.jpg"),
  music: require("../music.jpg")
};
class BandInfo extends React.Component {
  constructor() {
    super();
    this.state = {
      bandName: "",
      genres: ""
    };
  }
  componentDidMount() {
    fetch(`${bandURL}${this.props.band.id}`, {
      method: "GET",
      headers: {
        Authorization: `Bearer ${window.localStorage.getItem("token")}`
      }
    })
      .then(res => res.json())
      .then(data => {
        this.setState({
          bandName: data.band.name,
          genres: data.genres
        });
      });
  }
  genrearrayEmpty = name => {
    if (this.state.genres.length > 0) {
      let cardgenre = this.state.genres[0].name
        .replace(/\s/g, "")
        .toLowerCase();
      genresarray.map(genre => {
        if ((genre = cardgenre)) {
          return genre.value;
        }
      });
    } else {
      return console.log(music.value);
    }
  };

  render() {
    return (
      <div
        id={this.props.band.id}
        onClick={event => this.props.handleClick(event)}
      >
        <Card
          style={({ width: "18rem" }, { height: "10rem" })}
          className="card-footer"
          border="dark"
          text="white"
          key={this.props.band.id}
        >
          <Card.Img variant="top" src={this.genrearrayEmpty} />
          <Card.Body id={this.props.band.id}>
            <Card.Text>{this.props.band.name}</Card.Text>
          </Card.Body>
          <Card.Body>
            <Card.Text>{this.genrearrayEmpty()}</Card.Text>
          </Card.Body>
        </Card>
      </div>
    );
  }
}
export default BandInfo;
