import React from "react";
import moment from "moment";
import Card from "react-bootstrap/Card";
import VenuesList from "./VenuesList.js";
const showsURL = "http://localhost:3000/shows";
let array = [];
class Testing extends React.Component {
  constructor() {
    super();
    this.state = {
      shows: []
    };
  }

  componentDidMount() {
    fetch(showsURL, {
      method: "GET",
      headers: {
        Authorization: `Bearer ${window.localStorage.getItem("token")}`
      }
    })
      .then(res => res.json())
      .then(data => {
        this.setState({
          shows: data.shows
        });
      });
  }
  linkToBand = event => {
    window.localStorage.setItem("bandID", event.target.id);
    window.location.assign(`http://localhost:3001/BandProfile`);
  };

  render() {
    return (
      <div>
        {this.state.shows.map(show => {
          for (let i = 0; i < this.props.bands.length; i++) {
            if (this.props.bands[i].id === show.band_id) {
              return (
                <Card>
                  <Card.Body>
                    <Card.Title>
                      {moment(show.time).format(" hh:mm A, MMMM Do, YYYY ")}
                    </Card.Title>
                    <Card.Text>{this.props.bands[i].name}</Card.Text>

                    <VenuesList
                      show={show}
                      band={this.props.bands[i]}
                      LinkToBand={this.LinkToBand}
                    />
                  </Card.Body>
                  {/* <Button
                variant="outline-dark"
                id={this.props.bands[i].id}
                onClick={event => this.linkToBand(event)}
              >
                Band Profile page
              </Button> */}
                </Card>
              );
            }
          }
        })}
      </div>
    );
  }
}
export default Testing;
