import React, { Component } from "react";

const authURL = "http://localhost:3000/login";

class Home extends Component {
  constructor() {
    super();
    this.state = {
      name: "",
      password: ""
    };
  }

  handleChange = event => {
    event.persist();
    this.setState({ [event.target.name]: event.target.value });
  };
  handleSubmit = event => {
    event.preventDefault();
    const configOBJ = {
      method: "POST",
      headers: {
        "content-type": "application/json",
        accept: "application/json"
      },
      body: JSON.stringify(this.state)
    };
    // fetch(authURL, configOBJ)
    //   .then(res => res.json())
    //   .then(json => {
    //     console.log(json);
    //     window.localStorage.setItem("token", json.jwt);
    //     window.localStorage.setItem("name", json.user.name);
    //     window.localStorage.setItem("email", json.user.email);
    //     window.localStorage.setItem("id", `${json.user.id}`);
    //     window.localStorage.setItem("wallet", `${json.user.wallet}`);
    //     window.location.assign("http://localhost:3001");
    //   });
    this.setState({
      name: "",
      email: "",
      password: "",
      wallet: 1
    });
  };
  render() {
    console.log(window.localStorage.getItem("name"));
    return (
      <form onSubmit={event => this.handleSubmit(event)}>
        <div>
          <input
            type="text"
            name="name"
            onChange={event => this.handleChange(event)}
            value={this.state.name}
            placeholder="name"
          />
          <label htmlFor="name">name</label>
        </div>
        <div>
          <input
            type="password"
            name="password"
            onChange={event => this.handleChange(event)}
            value={this.state.password}
            placeholder="Password"
          />
          <label htmlFor="password">Password</label>
        </div>
        <input type="submit" value="Log in" />
      </form>
    );
  }
}
export default Home;
