import React from "react";
import Carousel from "react-bootstrap/Carousel";
const bandURL = "http://localhost:3000/bands/";

class FunCarousel extends React.Component {
  constructor() {
    super();
    this.state = {
      band: "",
      shows: [],
      venues: [],
      genres: []
    };
  }
  componentDidMount() {
    fetch(`${bandURL}${this.props.bandid}`, {
      method: "GET",
      headers: {
        Authorization: `Bearer ${window.localStorage.getItem("token")}`
      }
    })
      .then(res => res.json())
      .then(json => {
        this.setState({
          band: json.band.name,
          shows: json.shows,
          venues: json.venues,
          genres: json.genres
        });
        console.log(this.state.band);
        console.log(this.state.shows);
        console.log(this.state.venues);
        console.log(this.state.genres);
      });
  }
  render() {
    return <Carousel></Carousel>;
  }
}
export default FunCarousel;
