import React from "react";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/row";
import Col from "react-bootstrap/Col";
const BandShowURL = "http://localhost:3000/bands/";
class BandProfile extends React.Component {
  constructor() {
    super();
    this.state = {
      band: [],
      venues: [],
      shows: [],
      genres: []
    };
  }
  componentDidMount() {
    fetch(`${BandShowURL}${window.localStorage.getItem("bandID")}`, {
      method: "GET",
      headers: {
        Authorization: `"Bearer ${window.localStorage.getItem("token")}`
      }
    })
      .then(res => res.json())
      .then(data => {
        this.setState({
          band: data.band,
          venues: data.venues,
          shows: data.shows,
          genres: data.genres
        });
      });
  }
  venueList = () => {
    this.state.venues.map(venue => {
      console.log(venue.name);
    });
  };
  showList = () => {
    this.state.shows.map(show => {
      return show;
    });
  };
  genresList = () => {
    this.state.genres.map(genre => {
      console.log(genre.name);
    });
  };
  render() {
    return (
      <div>
        {console.log(this.genres)}
        {console.log(this.state.band)}
        <Container>
          <Row>
            <Col>{this.state.band.name}</Col>
          </Row>
          <Row>
            <Col>{this.showList()}</Col>
            <Col>{this.venueList()}</Col>
          </Row>
        </Container>
      </div>
    );
  }
}
export default BandProfile;
