import React from "react";
import Modal from "react-bootstrap/Modal";
import ModalHeader from "react-bootstrap/ModalHeader";
import ModalBody from "react-bootstrap/ModalBody";
import ModalFooter from "react-bootstrap/ModalFooter";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
const userbandsURL = "http://localhost:3000/userbands";
const BandShowURL = "http://localhost:3000/bands/";
const userURL = "http://localhost:3000/users/";
class Tip extends React.Component {
  constructor() {
    super();
    this.state = {
      userwallet: 0,
      bandwallet: 0,
      tipamount: 0,
      modal: false
    };
  }
  componentWillMount() {
    fetch(`${userURL}${window.localStorage.getItem("id")}`, {
      method: "GET",
      headers: {
        Authorization: `Bearer ${window.localStorage.getItem("token")}`
      }
    })
      .then(res => res.json())
      .then(data => {
        this.setState({
          userwallet: data.wallet
        });
      });
  }
  toggleModal = () => {
    this.setState({
      modal: true
    });
  };

  handleChange = () => {
    this.setState({
      tipamount: 0
    });
  };
  handleSubmit = () => {
    if (this.state.userwallet >= this.state.tipamount) {
      let body = this.state;
      body.bandwallet = parseInt(this.state.tipamount);
      body.id = parseInt(window.localStorage.getItem("bandID"));
      let configObj = {
        method: "PATCH",
        headers: {
          "content-type": "application/json",
          accept: "application/json",
          Authorization: `Bearer ${window.localStorage.getItem("token")}`
        },
        body: JSON.stringify(body)
      };

      fetch(`${BandShowURL}${window.localStorage.getItem("bandID")}`, configObj)
        .then(res => res.json())
        .then(json => {
          this.setState({
            bandwallet: json.wallet
          });
          window.location.reload();
        });
    }
  };
  componentDidMount() {}
  render() {
    return (
      <div>
        <Button onClick={this.toggleModal()} variant="outline-dark">
          Send Some Love
        </Button>
        <Modal>
          <ModalHeader>Show Your Support!</ModalHeader>
          <ModalBody>
            <form onSubmit={event => this.handleSubmit(event)}>
              <Form.Group>
                <Form.Control
                  type="text"
                  name="tip"
                  onChange={event => this.handleChange(event)}
                  value={this.state.tipamount}
                  placeholder="tip"
                />
                <div>
                  <Button
                    variant="outline-dark"
                    input
                    type="submit"
                    value="tip"
                  >
                    Send It!
                  </Button>
                </div>
              </Form.Group>
            </form>
          </ModalBody>
        </Modal>
      </div>
    );
  }
}
export default Tip;
