import React from "react";
import Modal from "react-bootstrap/Modal";
import ModalHeader from "react-bootstrap/ModalHeader";
import ModalBody from "react-bootstrap/ModalBody";
import ModalFooter from "react-bootstrap/ModalFooter";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
class TipModal extends React.Component {
  constructor() {
    super();
    this.state = {
      bandwallet: 0,
      TipAmount: 0,
      modal: true
    };
  }

  handleChange = event => {
    event.persist();
    this.setState({
      TipAmount: event.target.value
    });
  };
  handleSubmit = event => {
    event.preventDefault();
    console.log(typeof this.state.TipAmount);
    console.log(typeof this.props.user);
    this.setState({
      modal: !this.state.modal
    });
  };

  render() {
    return (
      <div>
        {console.log(this.state.TipAmount)}
        <Modal show={this.state.modal} animation={true}>
          <ModalHeader>Be Groovy send some Cash </ModalHeader>
          <Form.Group
            onSubmit={event => {
              this.handleSubmit(event);
            }}
          >
            {" "}
            <ModalBody>
              <Form onSubmit={event => this.handleSubmit(event)}>
                <Form.Control
                  type="integer"
                  name="tip"
                  onChange={event => this.handleChange(event)}
                  value={this.state.tipAmount}
                  placeholder="tip"
                />
                <Button variant="outline-dark" input type="submit" value="tip">
                  Tip!
                </Button>
                <Button>Close</Button>
              </Form>
            </ModalBody>{" "}
          </Form.Group>
        </Modal>
      </div>
    );
  }
}
export default TipModal;
