import React from "react";
const bandURL = "http://localhost:3000/bands/";
class BandInfo extends React.Component {
  render() {
    return (
      <div>
        {this.props.item.map(id =>
          fetch(`${bandURL}${id}`)
            .then(res => res.json())
            .then(data => {
              console.log(data);
            })
        )}
      </div>
    );
  }
}
export default BandInfo;
