import React from "react";
const bandURL = "http://localhost:3000/bands/";
class Carousel extends React.Component {
  constructor() {
    super();
    this.state = {
      band: "",
      shows: [],
      venues: [],
      genres: []
    };
  }
  componentDidMount() {
    fetch(`${bandURL}${this.props.bandid}`, {
      method: "GET",
      headers: {
        Authorization: `Bearer ${window.localStorage.getItem("token")}`
      }
    })
      .then(res => res.json())
      .then(json => {
        this.setState({
          band: json.band.name,
          shows: json.shows,
          venues: json.venues,
          genres: json.genres
        });
      });
  }
  render() {
    return <div></div>;
  }
}
export default Carousel;
