import React, { Component } from "react";
import Button from "react-bootstrap/Button";

import Modal from "react-bootstrap/Modal";
import ModalHeader from "react-bootstrap/ModalHeader";
import ModalBody from "react-bootstrap/ModalBody";
import ModalFooter from "react-bootstrap/ModalFooter";
class AddWallet extends Component {
  constructor() {
    super();
    this.state = {
      modalIsOpen: false,
      wallet: 0
    };
  }
  componentDidMount() {
    this.setState({
      wallet: this.props.wallet
    });
  }

  toggleModal = () => {
    this.setState({
      modalIsOpen: !this.state.modalIsOpen
    });
  };

  handleChange = event => {
    event.persist();
    this.setState({});
  };
  handleSubmit = event => {};
  render() {
    console.log(this.state.modalIsOpen);
    return (
      <div>
        <div>
          <Button onClick={this.toggleModal}>hello</Button>
        </div>
        <Modal show={this.state.modalIsOpen}>
          <ModalHeader>Add Funds</ModalHeader>
          <ModalBody>
            <form onSubmit={event => this.handleSubmit(event)}></form>
          </ModalBody>
          <ModalFooter>
            <Button onClick={this.toggleModal}>submit</Button>
            <Button onClick={this.toggleModal}>back</Button>
          </ModalFooter>
        </Modal>
      </div>
    );
  }
}
export default AddWallet;
