import React from "react";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/row";
import Col from "react-bootstrap/Col";
import Button from "react-bootstrap/Button";
const BandShowURL = "http://localhost:3000/bands/";
class BandProfile extends React.Component {
  constructor() {
    super();
    this.state = {
      band: [],
      venues: [],
      shows: [],
      genres: []
    };
  }
  componentDidMount() {
    fetch(`${BandShowURL}${window.localStorage.getItem("bandID")}`, {
      method: "GET",
      headers: {
        Authorization: `"Bearer ${window.localStorage.getItem("token")}`
      }
    })
      .then(res => res.json())
      .then(data => {
        this.setState({
          band: data.band,
          venues: data.venues,
          shows: data.shows,
          genres: data.genres
        });
      });
  }
  followFunction = () => {
    event.preventDefault();
    let band_id = parseInt(this.state.band.id);
    let user_id = parseInt(window.localStorage.getItem("id"));
  };

  //   handleSubmit = event => {
  //     event.preventDefault();
  //     let body = this.state;
  //     body.user_id = parseInt(window.localStorage.getItem("id"));
  //     body.round_id = parseInt(this.props.round.id);
  //     const configObj = {
  //       method: "POST",
  //       headers: {
  //         "content-type": "application/json",
  //         accept: "application/json",
  //         Authorization: `Bearer ${window.localStorage.getItem("token")}`
  //       },
  //       body: JSON.stringify(body)
  //     };
  //     fetch(betsURL, configObj)
  //       .then(res => res.json())
  //       .then(json => {
  //         console.log(json);
  //       })
  //       .catch(error => console.log(error));
  //     this.props.viewRound();
  //   };
  render() {
    return (
      <div>
        <Container>
          <Row>
            <Col>{this.state.band.name}</Col>
            <Col>
              {this.state.genres.map(genre => {
                return genre.name;
              })}
            </Col>
          </Row>
          <Row>
            <Col>
              <li>
                {this.state.venues.map(venue => {
                  return venue.name;
                })}
              </li>
            </Col>
            <Col>
              {this.state.shows.map(show => {
                return show.time;
              })}
            </Col>
          </Row>
        </Container>
        <Button variant="outline-dark" onClick={this.followFunction}>
          Follow!
        </Button>
      </div>
    );
  }
}
export default BandProfile;
