import React from "react";
import moment from "moment";
import Card from "react-bootstrap/Card";
const showsURL = "http://localhost:3000/shows";

class Testing extends React.Component {
  constructor() {
    super();
    this.state = {
      shows: []
    };
  }

  componentDidMount() {
    fetch(showsURL, {
      method: "GET",
      headers: {
        Authorization: `Bearer ${window.localStorage.getItem("token")}`
      }
    })
      .then(res => res.json())
      .then(data => {
        this.setState({
          shows: data.shows
        });
      });
  }
  setbands = () => {
    let array = [];
    this.props.map(band => {
      console.log(band);
    });
  };

  render() {
    return (
      <div>
        {this.state.shows.map(show => {
          //   console.log(moment(show.time).format(" hh:mm A, MMMM Do, YYYY "));
          return moment(show.time).format(" hh:mm A, MMMM Do, YYYY ");
        })}
      </div>
    );
  }
}
export default Testing;
