import React from "react";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/row";
import Col from "react-bootstrap/Col";
import Button from "react-bootstrap/Button";
const BandShowURL = "http://localhost:3000/bands/";
class BandProfile extends React.Component {
  constructor() {
    super();
    this.state = {
      band: [],
      venues: [],
      shows: [],
      genres: []
    };
  }
  componentDidMount() {
    fetch(`${BandShowURL}${window.localStorage.getItem("bandID")}`, {
      method: "GET",
      headers: {
        Authorization: `"Bearer ${window.localStorage.getItem("token")}`
      }
    })
      .then(res => res.json())
      .then(data => {
        this.setState({
          band: data.band,
          venues: data.venues,
          shows: data.shows,
          genres: data.genres
        });
      });
  }
  followFunction = () => {};
  //   venueList = () => {
  //     this.state.venues.map(venue => {
  //       return <li>{venue}</li>;
  //     });
  //   };
  //   showList = () => {
  //     this.state.shows.map(show => {
  //       return <h3> {show.time}</h3>;
  //     });
  //   };
  //   genresList = () => {
  //     this.state.genres.map(genre => {
  //       console.log(genre.name);
  //     });
  //   };
  render() {
    return (
      <div>
        <Container>
          <Row>
            <Col>{this.state.band.name}</Col>
            <Col>
              {this.state.genres.map(genre => {
                return genre.name;
              })}
            </Col>
          </Row>
          <Row>
            <Col>
              <li>
                {this.state.venues.map(venue => {
                  return venue.name;
                })}
              </li>
            </Col>
            <Col>
              {this.state.shows.map(show => {
                return show.time;
              })}
            </Col>
          </Row>
        </Container>
        <Button variant="outline-dark" onClick={this.followFunction}>
          Follow!
        </Button>
      </div>
    );
  }
}
export default BandProfile;
