import React from "react";
import Card from "react-bootstrap/Card";
import CardDeck from "react-bootstrap/CardGroup";
import moment from "moment";

const BandShowURL = "http://localhost:3000/bands/";

class MainpageCard extends React.Component {
  constructor() {
    super();
    this.state = {
      details: [],
      banddet: [],
      venuedet: [],
      showdet: [],
      genredet: []
    };
  }
  componentDidMount() {
    fetch(`${BandShowURL}${this.props.band}`, {
      method: "GET",
      headers: {
        Authorization: `Bearer ${window.localStorage.getItem("token")}`
      }
    })
      .then(res => res.json())
      .then(data => {
        this.setState({
          details: data,
          banddet: data.band,
          showdet: data.shows,
          venuedet: data.venues,
          genredet: data.genres
        });
      });
  }

  render() {
    const timez = this.state.showdet.time;
    console.log(timez);
    const sortedList = this.state.showdet.sort((a, b) => a.time - b.time);
    return (
      <div>
        <Card>
          {/* {this.state.banddet.name

              {this.state.genredet.map(genre => {
                return genre.name;
              })}

              <ul>
                {this.state.venuedet.map(venue => {
                  return (
                    <li>
                      {venue.name} | {venue.location}
                      <br></br>
                      {sortedList.map(show => {
                        if (show.venue_id === venue.id)
                          return moment(show.time).format(
                            " hh:mm A, MMMM Do, YYYY "
                          );
                      })}
                    </li>
                  );
                })} */}
          <ul>
            {sortedList.map(show => {
              return (
                <Card.Title>
                  {moment(show.time).format(" hh:mm A, MMMM Do, YYYY ")}
                </Card.Title>
              );
            })}
          </ul>

          <Card.Footer>bandprofile page</Card.Footer>
        </Card>
      </div>
    );
  }
}
export default MainpageCard;
