import React, { Component } from "react";
import Goggle from "../components/Goggle.js";
import CardDeck from "react-bootstrap/CardGroup";
import MainpageCard from "../components/MainpageCard.js";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
const profileURL = "http://localhost:3000/profile";
const BandShowURL = "http://localhost:3000/bands/";

class MainPage extends Component {
  constructor() {
    super();
    this.state = {
      user: [],
      bands: [],
      Clicked: false
    };
  }
  componentDidMount() {
    fetch(profileURL, {
      method: "GET",
      headers: {
        Authorization: `Bearer ${window.localStorage.getItem("token")}`
      }
    })
      .then(res => res.json())
      .then(data => {
        this.setState({
          user: data.user,
          bands: data.bands
        });
      });
  }
  handleLink = (event, lat, lng) => {
    console.log(event.target);
    console.log(lat);
    console.log(lng);
    this.setState({
      Clicked: !this.state.Clicked
    });
  };

  render() {
    return (
      <div>
        <div>
          <h1>Welcome back, {window.localStorage.getItem("name")}!</h1>
          <CardDeck>
            {this.state.bands.map(band => {
              {
              }
              return (
                <MainpageCard band={band.id} handleLink={this.handleLink} />
              );
            })}
          </CardDeck>
        </div>
        {this.state.Clicked === true ? <Goggle /> : console.log("what up nerd")}
      </div>
    );
  }
}
export default MainPage;
