import React from "react";
const bandURL = "http://localhost:3000/bands/1";
class BandInfo extends React.Component {
  constructor() {
    super();
    this.state = {
      user: [],
      band: [],
      band_id: [],
      shows: [],
      venues: []
    };
  }
  componentDidMount() {
    fetch(bandURL, {
      method: "GET",
      headers: {
        Authorization: `Bearer ${window.localStorage.getItem("token")}`
      }
    })
      .then(res => res.json())
      .then(data => {
        this.setState({
          user: window.localStorage.getItem("id"),
          band: data.name,
          band_id: data.id,
          shows: data.shows,
          venues: data.venues
        });
      });
  }
  render() {
    return <div>{console.log(this.props.item.shows)}</div>;
  }
}
export default BandInfo;
