import React from "react";
import moment from "moment";
import Card from "react/bootstrap/Card";
const showsURL = "http://localhost:3000/shows";

class Testing extends React.Component {
  constructor() {
    super();
    this.state = {
      shows: []
    };
  }

  componentDidMount() {
    fetch(showsURL, {
      method: "GET",
      headers: {
        Authorization: `Bearer ${window.localStorage.getItem("token")}`
      }
    })
      .then(res => res.json())
      .then(data => {
        this.setState({
          shows: data.shows
        });
      });
  }

  render() {
    return (
      <div>
        <Card>
          <Card.Body>
            {this.state.shows.map(show => {
              console.log(moment(show.time).format(" hh:mm A, MMMM Do, YYYY "));
              <Card.Title>
                {show.time.format(" hh:mm A, MMMM Do, YYYY ")}
              </Card.Title>;

              {
                console.log(`venue ${show.venue_id}`);
              }

              <Card.Text>{console.log(`band ${show.band_id}`)}</Card.Text>;
            })}
          </Card.Body>
        </Card>
      </div>
    );
  }
}
export default Testing;
