import React from "react";
import BandInfo from "./bandInfo.js";
import CardGroup from "react-bootstrap/CardGroup";
class BandCard extends React.Component {
  handleClick = event => {
    window.localStorage.setItem("bandID", event.target.id);
    window.location.assign("http://localhost3001/BandProfile");
  };
  render() {
    return (
      <div>
        <CardGroup>
          {this.props.bands.map(band => (
            <BandInfo band={band} handleClick={this.handleClick} />
          ))}
        </CardGroup>
      </div>
    );
  }
}
export default BandCard;
