import React from "react";
const bandURL = "http://localhost:3000/bands/";
class BandInfo extends React.Component {
  componentDidMount() {
    this.props.item.map(id =>
      fetch(`${bandURL}${id}`)
        .then(res => res.json())
        .then(data => {
          console.log(data);
        })
    );
  }

  render() {
    return <div>{console.log(this.props.item)}</div>;
  }
}
export default BandInfo;
